            if "Games" in qual_type or qual_type == "Teams" or qual_type == "Opponents":
                dates = [row["DateTime"] for row in match]
                match_count = len(dates)
                start_date = min(dates)
                end_date = max(dates)

                date_start_link = None
                date_end_link = None
