    if not count_div:
        if not comments:
            comments = player_page.find_all(string=lambda text: isinstance(text, Comment))
        for c in comments:
            temp_soup = BeautifulSoup(c, "lxml")
            temp_div = temp_soup.find("div", id="div_leaderboard")
