    valid_teams_key = {}
    valid_teams_raw_key = {}
    valid_year_teams = {}

    table_names = ["stats_basic_plus_nhl", "stats_basic_nhl", "stats_basic_plus_nhl_po", "stats_basic_nhl_po"]
